const TaiwanMap = new Vue({
  el: '#app',
  data: {
    h1: '福爾摩沙',
    h2: '美麗天堂島'
  },
  methods: {
    async getTaiwanMap() {
      const width = (this.$refs.map.offsetWidth).toFixed(),
            height = (this.$refs.map.offsetHeight).toFixed();

      // 判斷螢幕寬度，給不同放大值
      let mercatorScale, w = window.screen.width;
      if(w > 1366) {
        mercatorScale = 11000;
      }
      else if(w <= 1366 && w > 480) {
        mercatorScale = 9000;
      }
      else {
        mercatorScale = 6000;
	}

      // d3：svg path 產生器
      var path = await d3.geo.path().projection(
        // !important 座標變換函式
        d3.geo
          .mercator()             //  繪製地圖之前，要先選擇投影的方式，d3提供了很多的選擇。這次使用的方式是Mercator，也就是麥卡托投影法，可以用來表達正確的方位。
          .center([121,24])       //  center:決定地圖中心點的座標，之後放大縮小也是以此為中心點 scale:縮放倍數
          .scale(mercatorScale)
          .translate([width/1.3, height/2.5])     // 原來是 width/2, height/2.5
      );
      
      // 讓d3抓svg，並寫入寬高
      var svg = await d3.select('#svg')
          .attr('width', width)
          .attr('height', height)
          .attr('viewBox', `0 0 ${width} ${height}`);
      
      // 讓d3抓GeoJSON檔，並寫入path的路徑
      var url = 'dist/taiwan.geojson';
      await d3.json(url, (error, geometry) => {
        if (error) throw error;

        svg
          .selectAll('path')
          .data(geometry.features)
          .enter().append('path')
          .attr('d', path)            // 相當於 .attr("d", function(d){return path(d);}) 
          .attr({
            // 設定id，為了click時加class用
            id: (d) => 'city' + d.properties.COUNTYCODE
          })
          .on('mouseover', d => {              // 原來是 on 'click' 換成  mouseover
            this.h1 = d.properties.COUNTYNAME; // 換中文名         
            this.h2 = d.properties.POPULATION; // 換人口
            // 有 .active 存在，就移除 .active
           /*           
		   if(document.querySelector('.active')) {
              document.querySelector('.active').classList.remove('active');
            }
            */
            // 被點擊的縣市加上 .active
            document.getElementById('city' + d.properties.COUNTYCODE).classList.add('active');
		   /* 
	         d3.json(jsonurl, function(err, geojson) {	
	         var map = d3.select("g").selectAll("path")
		     .data(geojson.features)
		     .enter()
		     .append("path")
		     .attr("d", path)
		     .attr('stroke','black')
		     .attr('stroke-width', "1")
		     .attr('fill','#184200')
             .on("mouseover", function(d) {
			      d3.select(this).attr("fill", "#e0f9d1");		
		      })
		     .on("mouseleave", function(d) {
			    d3.select(this).attr("fill", "#184200");
		     });
             });
			*/		
		
          });
      });
      return svg;
    },
  },
  mounted() {

    this.getTaiwanMap();

  }
})